import React, { useState, useRef, useEffect } from "react";
import rrwebPlayer from "rrweb-player";
import "rrweb-player/dist/style.css";
import "./App.css";
import {
  cutRecording,
  analyzeRecording,
  findEventsByContent,
  timestampToRelativeTime,
  formatTime,
  parseTimeString,
} from "./RecordingCutter";

function App() {
  const [events, setEvents] = useState(null);
  const [error, setError] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [recording, setRecording] = useState(null); // Analysis of current recording
  const [cutTimestamp, setCutTimestamp] = useState("");
  const [beforeSeconds, setBeforeSeconds] = useState(5);
  const [afterSeconds, setAfterSeconds] = useState(5);
  const [searchTerm, setSearchTerm] = useState("");
  const [foundEvents, setFoundEvents] = useState([]);
  const playerRef = useRef(null);
  const replayerRef = useRef(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const jsonData = JSON.parse(e.target.result);
        if (Array.isArray(jsonData) && jsonData.length > 0) {
          setEvents(jsonData);
          setRecording(analyzeRecording(jsonData));
          setError(null);
        } else {
          setError("File doesn't contain valid rrweb events array.");
          setEvents(null);
          setRecording(null);
        }
      } catch (err) {
        setError(
          `Invalid JSON format: ${err.message}. Please select a valid rrweb events file.`
        );
        setEvents(null);
      }
    };
    reader.readAsText(file);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const jsonData = JSON.parse(e.target.result);
          if (Array.isArray(jsonData) && jsonData.length > 0) {
            setEvents(jsonData);
            setRecording(analyzeRecording(jsonData));
            setError(null);
          } else {
            setError("File doesn't contain valid rrweb events array.");
            setEvents(null);
            setRecording(null);
          }
        } catch (err) {
          setError(
            `Invalid JSON format: ${err.message}. Please select a valid rrweb events file.`
          );
          setEvents(null);
        }
      };
      reader.readAsText(file);
    }
  };

  useEffect(() => {
    if (events && playerRef.current) {
      // Clear any existing replayer
      if (replayerRef.current) {
        replayerRef.current.destroy();
        replayerRef.current = null;
      }

      try {
        // Create new replayer with rrweb-player
        const player = new rrwebPlayer({
          target: playerRef.current,
          props: {
            events: events,
            width: 1024,
            height: 576,
            autoPlay: false,
            showController: true,
            speedOption: [1, 2, 4, 8],
          },
        });

        replayerRef.current = player;
        setIsPlaying(false);
      } catch (err) {
        console.error("Failed to create rrweb player:", err);
        setError(`Failed to create player: ${err.message}`);
      }
    }

    return () => {
      if (replayerRef.current) {
        try {
          replayerRef.current.destroy();
        } catch (err) {
          console.error("Failed to destroy player:", err);
        }
        replayerRef.current = null;
      }
    };
  }, [events]);

  const handlePlay = () => {
    if (replayerRef.current) {
      try {
        replayerRef.current.play();
        setIsPlaying(true);
      } catch (err) {
        console.error("Failed to play:", err);
      }
    }
  };

  const handlePause = () => {
    if (replayerRef.current) {
      try {
        replayerRef.current.pause();
        setIsPlaying(false);
      } catch (err) {
        console.error("Failed to pause:", err);
      }
    }
  };

  const handleReset = () => {
    if (replayerRef.current) {
      try {
        replayerRef.current.goto(0);
        setIsPlaying(false);
      } catch (err) {
        console.error("Failed to reset:", err);
      }
    }
  };

  const handleSearch = () => {
    if (!events || !searchTerm) return;
    const found = findEventsByContent(events, searchTerm);
    setFoundEvents(found);
  };

  const handleCutRecording = () => {
    if (!events || !cutTimestamp) return;

    const timeSeconds = parseTimeString(cutTimestamp);
    if (timeSeconds < 0 || timeSeconds > recording.duration) {
      setError(
        `Please enter a valid time between 0 and ${formatTime(
          recording.duration
        )}`
      );
      return;
    }

    try {
      const clippedEvents = cutRecording(
        events,
        timeSeconds,
        beforeSeconds,
        afterSeconds
      );

      // Download the clipped recording as JSON
      const blob = new Blob([JSON.stringify(clippedEvents, null, 2)], {
        type: "application/json",
      });
      const url = URL.createObjectURL(blob);
      const a = document.createElement("a");
      a.href = url;
      a.download = `recording_clip_${formatTime(
        timeSeconds
      )}_${beforeSeconds}s_${afterSeconds}s.json`;
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      URL.revokeObjectURL(url);

      setError(null);
    } catch (err) {
      setError(`Failed to cut recording: ${err.message}`);
    }
  };

  const handleLoadClip = () => {
    if (!events || !cutTimestamp) return;

    const timeSeconds = parseTimeString(cutTimestamp);
    if (timeSeconds < 0 || timeSeconds > recording.duration) {
      setError(
        `Please enter a valid time between 0 and ${formatTime(
          recording.duration
        )}`
      );
      return;
    }

    try {
      const clippedEvents = cutRecording(
        events,
        timeSeconds,
        beforeSeconds,
        afterSeconds
      );
      setEvents(clippedEvents);
      setRecording(analyzeRecording(clippedEvents));
      setError(null);
    } catch (err) {
      setError(`Failed to cut recording: ${err.message}`);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>rrweb Player</h1>
        <p>
          Upload or drag & drop your rrweb events JSON file to replay sessions
        </p>
      </header>

      <main className="App-main">
        <div className="upload-section">
          <div
            className="upload-area"
            onDragOver={handleDragOver}
            onDrop={handleDrop}
          >
            <input
              type="file"
              onChange={handleFileChange}
              className="file-input"
              id="file-input"
            />
            <label htmlFor="file-input" className="upload-label">
              <div className="upload-content">
                <div className="upload-icon">📁</div>
                <div className="upload-text">
                  <strong>Click to upload</strong> or drag and drop
                </div>
                <div className="upload-subtitle">
                  rrweb recording files (any format/extension)
                  <br />
                  <small>💡 Try drag & drop if file picker doesn't work</small>
                </div>
              </div>
            </label>
          </div>

          {error && <div className="error-message">❌ {error}</div>}
        </div>

        {events && (
          <div className="player-section">
            <div className="player-controls">
              <button onClick={handlePlay} disabled={isPlaying}>
                ▶️ Play
              </button>
              <button onClick={handlePause} disabled={!isPlaying}>
                ⏸️ Pause
              </button>
              <button onClick={handleReset}>⏹️ Reset</button>
            </div>

            <div className="player-container">
              <div ref={playerRef} className="rrweb-player"></div>
            </div>

            {recording && (
              <div className="recording-info">
                <h3>📊 Recording Info</h3>
                <div className="info-grid">
                  <div>Duration: {recording.duration.toFixed(1)}s</div>
                  <div>Events: {recording.totalEvents}</div>
                  <div>
                    Start: {new Date(recording.startTime).toLocaleTimeString()}
                  </div>
                  <div>
                    End: {new Date(recording.endTime).toLocaleTimeString()}
                  </div>
                </div>
              </div>
            )}

            <div className="cutting-section">
              <h3>✂️ Cut Recording</h3>

              <div className="search-section">
                <h4>🔍 Find Events</h4>
                <div className="search-controls">
                  <input
                    type="text"
                    placeholder="Search for text in events..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-input"
                  />
                  <button onClick={handleSearch} className="search-btn">
                    Search
                  </button>
                </div>

                {foundEvents.length > 0 && (
                  <div className="found-events">
                    <p>Found {foundEvents.length} events:</p>
                    <div className="events-list">
                      {foundEvents.slice(0, 10).map((event, index) => {
                        const relativeTime = timestampToRelativeTime(
                          events,
                          event.timestamp
                        );
                        return (
                          <div key={index} className="event-item">
                            <span
                              className="timestamp"
                              onClick={() =>
                                setCutTimestamp(
                                  Math.floor(relativeTime).toString()
                                )
                              }
                              title={`Click to use this time (${Math.floor(
                                relativeTime
                              )}s)`}
                            >
                              {formatTime(relativeTime)}
                            </span>
                            <span className="event-type">
                              Type: {event.type}
                            </span>
                          </div>
                        );
                      })}
                      {foundEvents.length > 10 && (
                        <p>... and {foundEvents.length - 10} more</p>
                      )}
                    </div>
                  </div>
                )}
              </div>

              <div className="cut-controls">
                <div className="time-inputs">
                  <div className="input-group">
                    <label>Center Time:</label>
                    <input
                      type="text"
                      placeholder="e.g. 30, 1:30, 2:15"
                      value={cutTimestamp}
                      onChange={(e) => setCutTimestamp(e.target.value)}
                      className="timestamp-input"
                    />
                    <small>
                      Format: seconds (30) or MM:SS (1:30) or HH:MM:SS
                    </small>
                  </div>

                  <div className="input-group">
                    <label>Seconds Before:</label>
                    <input
                      type="number"
                      min="1"
                      max="60"
                      value={beforeSeconds}
                      onChange={(e) =>
                        setBeforeSeconds(parseInt(e.target.value) || 5)
                      }
                      className="duration-input"
                    />
                  </div>

                  <div className="input-group">
                    <label>Seconds After:</label>
                    <input
                      type="number"
                      min="1"
                      max="60"
                      value={afterSeconds}
                      onChange={(e) =>
                        setAfterSeconds(parseInt(e.target.value) || 5)
                      }
                      className="duration-input"
                    />
                  </div>
                </div>

                <div className="cut-buttons">
                  <button onClick={handleLoadClip} className="load-clip-btn">
                    📺 Load Clip
                  </button>
                  <button onClick={handleCutRecording} className="download-btn">
                    💾 Download Clip
                  </button>
                </div>

                <p className="help-text">
                  💡 Click on times from search results to auto-fill the center
                  time. Use seconds (30) or MM:SS (1:30) format.
                </p>
              </div>
            </div>
          </div>
        )}

        {!events && !error && (
          <div className="placeholder">
            <h3>No recording loaded</h3>
            <p>Upload a JSON file containing rrweb events to start replaying</p>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
