// RecordingCutter.js - Utility for cutting rrweb recordings into smaller clips

/**
 * Converts relative seconds to absolute timestamp
 * @param {Array} events - rrweb events array
 * @param {number} relativeSeconds - Seconds from start of recording
 * @returns {number} Absolute timestamp
 */
export function relativeTimeToTimestamp(events, relativeSeconds) {
  if (!events || events.length === 0) return 0;
  const startTime = events[0].timestamp;
  return startTime + relativeSeconds * 1000;
}

/**
 * Converts absolute timestamp to relative seconds
 * @param {Array} events - rrweb events array
 * @param {number} timestamp - Absolute timestamp
 * @returns {number} Relative seconds from start
 */
export function timestampToRelativeTime(events, timestamp) {
  if (!events || events.length === 0) return 0;
  const startTime = events[0].timestamp;
  return (timestamp - startTime) / 1000;
}

/**
 * Formats seconds as MM:SS or HH:MM:SS
 * @param {number} seconds - Total seconds
 * @returns {string} Formatted time string
 */
export function formatTime(seconds) {
  const hours = Math.floor(seconds / 3600);
  const minutes = Math.floor(seconds % 3600 / 60);
  const secs = Math.floor(seconds % 60);
  if (hours > 0) {
    return `${hours}:${minutes.toString().padStart(2, "0")}:${secs.toString().padStart(2, "0")}`;
  }
  return `${minutes}:${secs.toString().padStart(2, "0")}`;
}

/**
 * Parses time string (MM:SS, HH:MM:SS, or just seconds) to total seconds
 * @param {string} timeStr - Time string like "1:30", "0:05", "90", etc.
 * @returns {number} Total seconds
 */
export function parseTimeString(timeStr) {
  if (!timeStr) return 0;

  // If it's just a number, treat as seconds
  if (/^\d+$/.test(timeStr)) {
    return parseInt(timeStr);
  }

  // Parse MM:SS or HH:MM:SS format
  const parts = timeStr.split(":").map(Number);
  if (parts.length === 2) {
    // MM:SS
    return parts[0] * 60 + parts[1];
  } else if (parts.length === 3) {
    // HH:MM:SS
    return parts[0] * 3600 + parts[1] * 60 + parts[2];
  }
  return 0;
}

/**
 * Creates a synthetic full snapshot by applying all incremental changes
 * up to a specific timestamp
 * @param {Array} events - Original rrweb events array
 * @param {Object} baseSnapshot - The base full snapshot to start from
 * @param {number} targetTimestamp - The timestamp to build the DOM state up to
 * @returns {Object} Synthetic full snapshot representing the DOM state at targetTimestamp
 */
function createSyntheticSnapshot(events, baseSnapshot, targetTimestamp) {
  // For now, let's use a simplified approach
  // We'll just return the base snapshot with adjusted timing
  // In the future, this could be enhanced to actually merge DOM changes
  console.log(`Creating synthetic snapshot at ${targetTimestamp} from base at ${baseSnapshot.timestamp}`);
  return {
    ...baseSnapshot,
    timestamp: targetTimestamp
  };
}

/**
 * Cuts an rrweb recording to show only events within a specific time range
 * @param {Array} events - Original rrweb events array
 * @param {number} centerTimeSeconds - Relative time in seconds from start of recording
 * @param {number} beforeSeconds - Seconds to include before the center time (default: 5)
 * @param {number} afterSeconds - Seconds to include after the center time (default: 5)
 * @returns {Array} New rrweb events array for the clipped recording
 */
export function cutRecording(events, centerTimeSeconds, beforeSeconds = 5, afterSeconds = 5) {
  if (!events || !Array.isArray(events) || events.length === 0) {
    throw new Error("Invalid events array provided");
  }

  // Convert relative time to absolute timestamp
  const centerTimestamp = relativeTimeToTimestamp(events, centerTimeSeconds);
  const beforeMs = beforeSeconds * 1000;
  const afterMs = afterSeconds * 1000;
  const startTime = centerTimestamp - beforeMs;
  const endTime = centerTimestamp + afterMs;
  console.log(`Cutting recording around ${formatTime(centerTimeSeconds)} (${centerTimeSeconds}s)`);
  console.log(`Time range: ${formatTime(centerTimeSeconds - beforeSeconds)} to ${formatTime(centerTimeSeconds + afterSeconds)}`);

  // Find the full snapshot that's closest to our cut time (before or after)
  let fullSnapshot = null;
  let bestDistance = Infinity;
  for (const event of events) {
    if (event.type === 2) {
      const distance = Math.abs(event.timestamp - startTime);
      if (distance < bestDistance) {
        fullSnapshot = event;
        bestDistance = distance;
      }
    }
  }
  if (!fullSnapshot) {
    throw new Error("No full snapshot found in recording");
  }
  console.log(`Using full snapshot at ${fullSnapshot.timestamp}, distance from cut: ${bestDistance}ms`);

  // If the full snapshot is after our start time, we need to adjust our approach
  if (fullSnapshot.timestamp > startTime) {
    console.log("Full snapshot is after cut time, including events from start to snapshot");
  }

  // Instead of synthetic snapshot, include all events from full snapshot
  // to build proper DOM state, but trim to reasonable size
  const maxBuildupTime = 30000; // 30 seconds max of buildup events
  const buildupStartTime = Math.max(fullSnapshot.timestamp, startTime - maxBuildupTime);

  // Get all events needed to build DOM state from full snapshot to our clip
  const buildupEvents = events.filter(event => {
    return event.timestamp >= fullSnapshot.timestamp && event.timestamp < startTime;
  });

  // Get events in our actual clip range
  const clippedEvents = events.filter(event => {
    return event.timestamp >= startTime && event.timestamp <= endTime;
  });

  // If we don't have enough events in clip range, expand slightly
  if (clippedEvents.length === 0) {
    const nextEvent = events.find(event => event.timestamp > startTime);
    if (nextEvent) {
      clippedEvents.push(nextEvent);
    }
  }

  // Create the new events array
  const newEvents = [];

  // Add the original full snapshot
  newEvents.push({
    ...fullSnapshot,
    timestamp: fullSnapshot.timestamp
  });

  // Add a subset of buildup events (most recent ones) to avoid too much overhead
  const recentBuildupEvents = buildupEvents.slice(-50); // Last 50 buildup events max
  for (const event of recentBuildupEvents) {
    newEvents.push({
      ...event,
      timestamp: event.timestamp
    });
  }

  // Add all events in our clip range
  for (const event of clippedEvents) {
    newEvents.push({
      ...event,
      timestamp: event.timestamp
    });
  }

  // Ensure we have at least 2 events total
  if (newEvents.length < 2) {
    const lastTimestamp = newEvents[newEvents.length - 1].timestamp;
    newEvents.push({
      type: 4,
      // Meta event
      data: {
        href: window.location.href
      },
      timestamp: lastTimestamp + 100
    });
  }

  // Sort by timestamp to ensure proper order
  newEvents.sort((a, b) => a.timestamp - b.timestamp);
  console.log(`Original recording: ${events.length} events`);
  console.log(`Clipped recording: ${newEvents.length} events`);
  console.log(`Time span: ${beforeSeconds + afterSeconds} seconds`);
  return newEvents;
}

/**
 * Finds all events of a specific type within a recording
 * @param {Array} events - rrweb events array
 * @param {number} eventType - Event type to search for (1=DOM, 2=FullSnapshot, 3=IncrementalSnapshot, etc.)
 * @returns {Array} Array of events matching the type
 */
export function findEventsByType(events, eventType) {
  return events.filter(event => event.type === eventType);
}

/**
 * Finds events containing specific text or attributes
 * @param {Array} events - rrweb events array
 * @param {string} searchTerm - Text to search for
 * @returns {Array} Array of events containing the search term
 */
export function findEventsByContent(events, searchTerm) {
  const matchingEvents = [];
  for (const event of events) {
    const eventStr = JSON.stringify(event).toLowerCase();
    if (eventStr.includes(searchTerm.toLowerCase())) {
      matchingEvents.push(event);
    }
  }
  return matchingEvents;
}

/**
 * Analyzes a recording and provides statistics
 * @param {Array} events - rrweb events array
 * @returns {Object} Recording statistics
 */
export function analyzeRecording(events) {
  if (!events || !Array.isArray(events) || events.length === 0) {
    return null;
  }
  const firstTimestamp = events[0].timestamp;
  const lastTimestamp = events[events.length - 1].timestamp;
  const duration = (lastTimestamp - firstTimestamp) / 1000; // in seconds

  const eventTypeCounts = {};
  for (const event of events) {
    eventTypeCounts[event.type] = (eventTypeCounts[event.type] || 0) + 1;
  }
  return {
    totalEvents: events.length,
    duration: duration,
    startTime: firstTimestamp,
    endTime: lastTimestamp,
    eventTypeCounts: eventTypeCounts,
    eventTypes: {
      1: "DomContentLoaded",
      2: "FullSnapshot",
      3: "IncrementalSnapshot",
      4: "Meta",
      5: "Custom",
      6: "Plugin"
    }
  };
}

/**
 * Creates multiple clips around specified timestamps
 * @param {Array} events - Original rrweb events array
 * @param {Array} timestamps - Array of timestamps to create clips around
 * @param {number} beforeSeconds - Seconds before each timestamp
 * @param {number} afterSeconds - Seconds after each timestamp
 * @returns {Array} Array of clipped recordings
 */
export function createMultipleClips(events, timestamps, beforeSeconds = 5, afterSeconds = 5) {
  const clips = [];
  for (let i = 0; i < timestamps.length; i++) {
    const timestamp = timestamps[i];
    try {
      const clip = cutRecording(events, timestamp, beforeSeconds, afterSeconds);
      clips.push({
        index: i,
        centerTimestamp: timestamp,
        events: clip,
        duration: beforeSeconds + afterSeconds
      });
    } catch (error) {
      console.error(`Failed to create clip ${i} around timestamp ${timestamp}:`, error);
    }
  }
  return clips;
}

