import React, { useState, useRef, useEffect } from "react";
import { Replayer } from "rrweb";
import "./App.css";
import {
  cutRecording,
  analyzeRecording,
  findEventsByContent,
  timestampToRelativeTime,
  formatTime,
  parseTimeString,
} from "./RecordingCutter";

function App() {
  const [events, setEvents] = useState(null);
  const [error, setError] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [recording, setRecording] = useState(null); // Analysis of current recording
  const [cutTimestamp, setCutTimestamp] = useState("");
  const [beforeSeconds, setBeforeSeconds] = useState(5);
  const [afterSeconds, setAfterSeconds] = useState(5);
  const [searchTerm, setSearchTerm] = useState("");
  const [foundEvents, setFoundEvents] = useState([]);
  const playerRef = useRef(null);
  const replayer = useRef(null);

  // Player timeline states
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [isTrimmingMode, setIsTrimmingMode] = useState(false);
  const [trimStart, setTrimStart] = useState(0);
  const [trimEnd, setTrimEnd] = useState(0);
  const timelineUpdateInterval = useRef(null);

  const handleFileUpload = (file) => {
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const content = e.target.result;
        let jsonData;

        if (typeof content === "string") {
          jsonData = JSON.parse(content);
        } else {
          // Handle binary content
          const text = new TextDecoder().decode(content);
          jsonData = JSON.parse(text);
        }

        // Debug: Log the raw data structure
        console.log("Raw JSON data:", jsonData);
        console.log("Data type:", typeof jsonData);
        console.log("Is array:", Array.isArray(jsonData));

        // Handle different rrweb recording formats
        let eventsArray;
        if (Array.isArray(jsonData)) {
          eventsArray = jsonData;
        } else if (jsonData.events && Array.isArray(jsonData.events)) {
          // Some recordings store events in a nested structure
          eventsArray = jsonData.events;
        } else if (jsonData.data && Array.isArray(jsonData.data)) {
          eventsArray = jsonData.data;
        } else {
          // Single event wrapped in array
          eventsArray = [jsonData];
        }

        console.log("Processed events array:", eventsArray.length, "events");
        console.log("First few events:", eventsArray.slice(0, 3));

        setEvents(eventsArray);
        setError(null);

        // Analyze the recording
        const analysis = analyzeRecording(eventsArray);
        setRecording(analysis);
        console.log("Recording loaded:", analysis);
      } catch (err) {
        console.error("Error parsing file:", err);
        setError(
          `Error loading file: ${err.message}. Please make sure the file contains valid rrweb recording data.`
        );
      }
    };

    reader.onerror = () => {
      setError("Error reading file. Please try again.");
    };

    // Try to read as text first, fall back to binary if needed
    reader.readAsText(file);
  };

  const handleFileDrop = (e) => {
    e.preventDefault();
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      handleFileUpload(files[0]);
    }
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const initializePlayer = () => {
    if (!events || !playerRef.current) return;

    try {
      // Clean up existing replayer with error handling
      if (replayer.current) {
        try {
          replayer.current.destroy();
        } catch (destroyError) {
          console.warn("Error destroying previous replayer:", destroyError);
          // Force cleanup by clearing the container
          if (playerRef.current) {
            playerRef.current.innerHTML = "";
          }
        }
        replayer.current = null;
      }

      // Ensure player container is clean
      if (playerRef.current) {
        playerRef.current.innerHTML = "";
      }

      // Debug: Check the events structure
      console.log("Events to replay:", events.length, "events");
      const fullSnapshots = events.filter((e) => e.type === 2);
      const domUpdates = events.filter((e) => e.type === 3);
      console.log("Full snapshots:", fullSnapshots.length);
      console.log("DOM updates:", domUpdates.length);

      if (fullSnapshots.length === 0) {
        setError(
          "No full snapshot found in recording. This recording may be incomplete."
        );
        return;
      }

      // Create new replayer instance with enhanced configuration
      replayer.current = new Replayer(events, {
        root: playerRef.current,
        skipInactive: false,
        showWarning: true,
        blockClass: "rr-block",
        liveMode: false,
        triggerFocus: false,
        showDebug: false,
        insertStyleRules: [
          ".rr-block { background: rgba(0, 0, 0, 0.1); }",
          "iframe { background: white !important; }",
        ],
      });

      // Add event listeners for debugging
      replayer.current.on("state-change", (state) => {
        console.log("Replayer state changed:", state);
      });

      replayer.current.on("resize", (size) => {
        console.log("Replayer resized:", size);
      });

      // Get the duration from the replayer
      const metadata = replayer.current.getMetaData();
      const totalDuration = metadata.totalTime;
      setDuration(totalDuration);
      setTrimEnd(totalDuration);

      console.log("Replayer initialized successfully");
    } catch (err) {
      console.error("Error initializing replayer:", err);
      setError(`Error initializing player: ${err.message}`);
    }
  };

  useEffect(() => {
    if (events && playerRef.current) {
      initializePlayer();
    }

    // Cleanup on unmount
    return () => {
      if (replayer.current) {
        try {
          replayer.current.destroy();
        } catch (destroyError) {
          console.warn("Error destroying replayer on cleanup:", destroyError);
          // Force cleanup by clearing the container
          if (playerRef.current) {
            playerRef.current.innerHTML = "";
          }
        }
        replayer.current = null;
      }
    };
  }, [events]);

  const handlePlay = () => {
    if (replayer.current) {
      replayer.current.play();
      setIsPlaying(true);
      // Start updating timeline
      timelineUpdateInterval.current = setInterval(() => {
        if (replayer.current) {
          const time = replayer.current.getCurrentTime();
          setCurrentTime(time);
        }
      }, 100);
    }
  };

  const handlePause = () => {
    if (replayer.current) {
      replayer.current.pause();
      setIsPlaying(false);
      // Stop updating timeline
      if (timelineUpdateInterval.current) {
        clearInterval(timelineUpdateInterval.current);
      }
    }
  };

  const handleStop = () => {
    if (replayer.current) {
      replayer.current.pause();
      // Reset to beginning
      replayer.current.play(0);
      replayer.current.pause();
      setIsPlaying(false);
      setCurrentTime(0);
      // Stop updating timeline
      if (timelineUpdateInterval.current) {
        clearInterval(timelineUpdateInterval.current);
      }
    }
  };

  const handleSeek = (time) => {
    if (replayer.current) {
      replayer.current.play(time);
      replayer.current.pause();
      setCurrentTime(time);
    }
  };

  const searchEvents = () => {
    if (!events || !searchTerm.trim()) {
      setFoundEvents([]);
      return;
    }

    const found = findEventsByContent(events, searchTerm.trim());
    setFoundEvents(found);
  };

  const fillTimestamp = (relativeSeconds) => {
    setCutTimestamp(formatTime(relativeSeconds));
  };

  const handleStartTrimming = () => {
    setIsTrimmingMode(true);
    setTrimStart(currentTime);
    setTrimEnd(duration);
  };

  const handleSaveTrim = () => {
    if (!events || !recording) {
      setError("No recording loaded");
      return;
    }

    try {
      // Convert milliseconds to seconds for the cutting function
      const startSeconds = trimStart / 1000;
      const endSeconds = trimEnd / 1000;
      const durationSeconds = endSeconds - startSeconds;
      const centerSeconds = startSeconds + durationSeconds / 2;
      const beforeAfterSeconds = durationSeconds / 2;

      const clip = cutRecording(
        events,
        centerSeconds,
        beforeAfterSeconds,
        beforeAfterSeconds
      );

      // Download the trimmed clip
      const dataStr =
        "data:text/json;charset=utf-8," +
        encodeURIComponent(JSON.stringify(clip, null, 2));
      const downloadAnchorNode = document.createElement("a");
      downloadAnchorNode.setAttribute("href", dataStr);
      downloadAnchorNode.setAttribute(
        "download",
        `trimmed_${formatTime(startSeconds)}_to_${formatTime(endSeconds)}.json`
      );
      document.body.appendChild(downloadAnchorNode);
      downloadAnchorNode.click();
      downloadAnchorNode.remove();

      setIsTrimmingMode(false);
      setError(null);
    } catch (err) {
      console.error("Error trimming recording:", err);
      setError(`Error trimming recording: ${err.message}`);
    }
  };

  const handleCancelTrim = () => {
    setIsTrimmingMode(false);
    setTrimStart(0);
    setTrimEnd(duration);
  };

  const handleTrimHandleChange = (type, value) => {
    if (type === "start") {
      setTrimStart(Math.max(0, Math.min(value, trimEnd - 1000))); // Ensure at least 1 second
    } else {
      setTrimEnd(Math.max(trimStart + 1000, Math.min(value, duration)));
    }
  };

  const handleCut = (action) => {
    if (!events || !recording) {
      setError("No recording loaded");
      return;
    }

    try {
      const relativeSeconds = parseTimeString(cutTimestamp);
      const clip = cutRecording(
        events,
        relativeSeconds,
        beforeSeconds,
        afterSeconds
      );

      if (action === "load") {
        // Replace current recording with the clip
        setEvents(clip);
        const analysis = analyzeRecording(clip);
        setRecording(analysis);
        setCutTimestamp("");
        setFoundEvents([]);
      } else if (action === "download") {
        // Download the clip as JSON
        const dataStr =
          "data:text/json;charset=utf-8," +
          encodeURIComponent(JSON.stringify(clip, null, 2));
        const downloadAnchorNode = document.createElement("a");
        downloadAnchorNode.setAttribute("href", dataStr);
        downloadAnchorNode.setAttribute(
          "download",
          `clip_${cutTimestamp || "segment"}.json`
        );
        document.body.appendChild(downloadAnchorNode);
        downloadAnchorNode.click();
        downloadAnchorNode.remove();
      }
    } catch (err) {
      console.error("Error cutting recording:", err);
      setError(`Error cutting recording: ${err.message}`);
    }
  };

  return (
    <div className="app">
      <div className="header">
        <h1>RRWeb Player</h1>
        <p>Upload an rrweb recording file to play it back</p>
      </div>

      <div className="upload-section">
        <div
          className="upload-area"
          onDrop={handleFileDrop}
          onDragOver={handleDragOver}
        >
          <input
            type="file"
            onChange={(e) => handleFileUpload(e.target.files[0])}
            className="file-input"
            id="file-upload"
          />
          <label htmlFor="file-upload" className="upload-label">
            <div className="upload-icon">📁</div>
            <div className="upload-text">
              <strong>Choose a file</strong> or drag it here
            </div>
            <div className="upload-subtext">
              Supports rrweb recording files (with or without .json extension)
            </div>
          </label>
        </div>
      </div>

      {error && <div className="error">{error}</div>}

      {recording && (
        <div className="recording-info">
          <h3>Recording Information</h3>
          <div className="recording-stats">
            <div className="stat">
              <span className="stat-label">Duration:</span>
              <span className="stat-value">
                {formatTime(recording.duration)}
              </span>
            </div>
            <div className="stat">
              <span className="stat-label">Events:</span>
              <span className="stat-value">{recording.eventCount}</span>
            </div>
            <div className="stat">
              <span className="stat-label">Start:</span>
              <span className="stat-value">
                {new Date(recording.startTime).toLocaleTimeString()}
              </span>
            </div>
            <div className="stat">
              <span className="stat-label">End:</span>
              <span className="stat-value">
                {new Date(recording.endTime).toLocaleTimeString()}
              </span>
            </div>
          </div>
        </div>
      )}

      {events && (
        <div className="player-section">
          <div ref={playerRef} className="player"></div>

          <div className="custom-controls">
            <div className="timeline-container">
              <div 
                className="timeline" 
                onClick={(e) => {
                  if (!isTrimmingMode) {
                    const rect = e.currentTarget.getBoundingClientRect();
                    const x = e.clientX - rect.left;
                    const percentage = x / rect.width;
                    const seekTime = percentage * duration;
                    handleSeek(seekTime);
                  }
                }}
              >
                <div className="timeline-progress" style={{ width: `${(currentTime / duration) * 100}%` }} />

                {isTrimmingMode && (
                  <>
                    <div 
                      className="trim-overlay trim-start"
                      style={{ width: `${(trimStart / duration) * 100}%` }}
                    />
                    <div 
                      className="trim-overlay trim-end"
                      style={{ left: `${(trimEnd / duration) * 100}%`, width: `${((duration - trimEnd) / duration) * 100}%` }}
                    />
                    <div 
                      className="trim-handle trim-handle-start"
                      style={{ left: `${(trimStart / duration) * 100}%` }}
                      onMouseDown={(e) => {
                        e.stopPropagation();
                        const startX = e.clientX;
                        const startValue = trimStart;
                        const rect = e.currentTarget.parentElement.getBoundingClientRect();

                        const handleMouseMove = (e) => {
                          const deltaX = e.clientX - startX;
                          const deltaPercent = deltaX / rect.width;
                          const newValue = startValue + (deltaPercent * duration);
                          handleTrimHandleChange('start', newValue);
                        };

                        const handleMouseUp = () => {
                          document.removeEventListener('mousemove', handleMouseMove);
                          document.removeEventListener('mouseup', handleMouseUp);
                        };

                        document.addEventListener('mousemove', handleMouseMove);
                        document.addEventListener('mouseup', handleMouseUp);
                      }}
                    />
                    <div 
                      className="trim-handle trim-handle-end"
                      style={{ left: `${(trimEnd / duration) * 100}%` }}
                      onMouseDown={(e) => {
                        e.stopPropagation();
                        const startX = e.clientX;
                        const startValue = trimEnd;
                        const rect = e.currentTarget.parentElement.getBoundingClientRect();

                        const handleMouseMove = (e) => {
                          const deltaX = e.clientX - startX;
                          const deltaPercent = deltaX / rect.width;
                          const newValue = startValue + (deltaPercent * duration);
                          handleTrimHandleChange('end', newValue);
                        };

                        const handleMouseUp = () => {
                          document.removeEventListener('mousemove', handleMouseMove);
                          document.removeEventListener('mouseup', handleMouseUp);
                        };

                        document.addEventListener('mousemove', handleMouseMove);
                        document.addEventListener('mouseup', handleMouseUp);
                      }}
                    />
                  </>
                )}
              </div>

              <div className="timeline-times">
                <span>{formatTime(currentTime / 1000)}</span>
                {isTrimmingMode && (
                  <span className="trim-info">
                    Trim: {formatTime(trimStart / 1000)} - {formatTime(trimEnd / 1000)}
                  </span>
                )}
                <span>{formatTime(duration / 1000)}</span>
              </div>
            </div>

            <div className="player-controls">
              <button onClick={handlePlay} disabled={isPlaying}>
                ▶️ Play
              </button>
              <button onClick={handlePause} disabled={!isPlaying}>
                ⏸️ Pause
              </button>
              <button onClick={handleStop}>⏹️ Stop</button>

              {!isTrimmingMode ? (
                <button onClick={handleStartTrimming} className="trim-button">
                  ✂️ Trim
                </button>
              ) : (
                <>
                  <button onClick={handleSaveTrim} className="save-trim-button">
                    💾 Save Trim
                  </button>
                  <button onClick={handleCancelTrim} className="cancel-trim-button">
                    ❌ Cancel
                  </button>
                </>
              )}
            </div>
          </div>
        </div>
      )}

      {events && (
        <div className="search-section">
          <h3>Search Events</h3>
          <div className="search-controls">
            <input
              type="text"
              placeholder="Search for text content in events..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
            <button onClick={searchEvents} className="search-button">
              Search
            </button>
          </div>
          {foundEvents.length > 0 && (
            <div className="search-results">
              <h4>Found {foundEvents.length} events:</h4>
              <div className="events-list">
                {foundEvents.slice(0, 10).map((event, index) => (
                  <div key={index} className="event-item">
                    <div className="event-time">
                      <strong>
                        {formatTime(
                          timestampToRelativeTime(event.timestamp, recording)
                        )}
                      </strong>
                    </div>
                    <div className="event-content">
                      {JSON.stringify(event).substring(0, 200)}...
                    </div>
                    <button
                      onClick={() =>
                        fillTimestamp(
                          timestampToRelativeTime(event.timestamp, recording)
                        )
                      }
                      className="fill-button"
                    >
                      Use Time
                    </button>
                  </div>
                ))}
                {foundEvents.length > 10 && (
                  <p>... and {foundEvents.length - 10} more</p>
                )}
              </div>
            </div>
          )}
        </div>
      )}

      {events && (
        <div className="cutting-section">
          <h3>Cut Recording</h3>
          <div className="cutting-controls">
            <div className="cutting-row">
              <label>
                Time (MM:SS or seconds):
                <input
                  type="text"
                  placeholder="1:30 or 90"
                  value={cutTimestamp}
                  onChange={(e) => setCutTimestamp(e.target.value)}
                  className="time-input"
                />
              </label>
              <label>
                Before (seconds):
                <input
                  type="number"
                  min="0"
                  max="60"
                  value={beforeSeconds}
                  onChange={(e) => setBeforeSeconds(Number(e.target.value))}
                  className="duration-input"
                />
              </label>
              <label>
                After (seconds):
                <input
                  type="number"
                  min="0"
                  max="60"
                  value={afterSeconds}
                  onChange={(e) => setAfterSeconds(Number(e.target.value))}
                  className="duration-input"
                />
              </label>
            </div>
            <div className="cutting-actions">
              <button
                onClick={() => handleCut("load")}
                className="cut-button primary"
              >
                Load Clip
              </button>
              <button
                onClick={() => handleCut("download")}
                className="cut-button secondary"
              >
                Download Clip
              </button>
            </div>
            <div className="cutting-help">
              <p>
                <strong>Instructions:</strong> Enter a time within the video
                duration ({recording ? formatTime(recording.duration) : "0:00"}
                ). The clip will include the specified seconds before and after
                that time.
              </p>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
