import React, { useState, useRef, useEffect } from "react";
import rrwebPlayer from "rrweb-player";
import "rrweb-player/dist/style.css";
import "./App.css";

function App() {
  const [events, setEvents] = useState(null);
  const [error, setError] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const playerRef = useRef(null);
  const replayerRef = useRef(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const jsonData = JSON.parse(e.target.result);
        if (Array.isArray(jsonData) && jsonData.length > 0) {
          setEvents(jsonData);
          setError(null);
        } else {
          setError("File doesn't contain valid rrweb events array.");
          setEvents(null);
        }
      } catch (err) {
        setError(
          `Invalid JSON format: ${err.message}. Please select a valid rrweb events file.`
        );
        setEvents(null);
      }
    };
    reader.readAsText(file);
  };

  const handleDragOver = (e) => {
    e.preventDefault();
  };

  const handleDrop = (e) => {
    e.preventDefault();
    const files = e.dataTransfer.files;
    if (files.length > 0) {
      const file = files[0];
      const reader = new FileReader();
      reader.onload = (e) => {
        try {
          const jsonData = JSON.parse(e.target.result);
          if (Array.isArray(jsonData) && jsonData.length > 0) {
            setEvents(jsonData);
            setError(null);
          } else {
            setError("File doesn't contain valid rrweb events array.");
            setEvents(null);
          }
        } catch (err) {
          setError(
            `Invalid JSON format: ${err.message}. Please select a valid rrweb events file.`
          );
          setEvents(null);
        }
      };
      reader.readAsText(file);
    }
  };

  useEffect(() => {
    if (events && playerRef.current) {
      // Clear any existing replayer
      if (replayerRef.current) {
        replayerRef.current.$destroy();
      }

      // Create new replayer
      replayerRef.current = new rrwebPlayer({
        target: playerRef.current,
        props: {
          events: events,
          width: 1024,
          height: 576,
          autoPlay: false,
          showController: true,
          speedOption: [1, 2, 4, 8],
        },
      });

      setIsPlaying(false);
    }

    return () => {
      if (replayerRef.current) {
        replayerRef.current.$destroy();
      }
    };
  }, [events]);

  const handlePlay = () => {
    if (replayerRef.current) {
      replayerRef.current.play();
      setIsPlaying(true);
    }
  };

  const handlePause = () => {
    if (replayerRef.current) {
      replayerRef.current.pause();
      setIsPlaying(false);
    }
  };

  const handleReset = () => {
    if (replayerRef.current) {
      replayerRef.current.goto(0, false);
      setIsPlaying(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>rrweb Player</h1>
        <p>
          Upload or drag & drop your rrweb events JSON file to replay sessions
        </p>
      </header>

      <main className="App-main">
        <div className="upload-section">
          <div
            className="upload-area"
            onDragOver={handleDragOver}
            onDrop={handleDrop}
          >
            <input
              type="file"
              onChange={handleFileChange}
              className="file-input"
              id="file-input"
            />
            <label htmlFor="file-input" className="upload-label">
              <div className="upload-content">
                <div className="upload-icon">üìÅ</div>
                <div className="upload-text">
                  <strong>Click to upload</strong> or drag and drop
                </div>
                <div className="upload-subtitle">
                  JSON files (any extension supported)
                </div>
              </div>
            </label>
          </div>

          {error && <div className="error-message">‚ùå {error}</div>}
        </div>

        {events && (
          <div className="player-section">
            <div className="player-controls">
              <button onClick={handlePlay} disabled={isPlaying}>
                ‚ñ∂Ô∏è Play
              </button>
              <button onClick={handlePause} disabled={!isPlaying}>
                ‚è∏Ô∏è Pause
              </button>
              <button onClick={handleReset}>‚èπÔ∏è Reset</button>
            </div>

            <div className="player-container">
              <div ref={playerRef} className="rrweb-player"></div>
            </div>
          </div>
        )}

        {!events && !error && (
          <div className="placeholder">
            <h3>No recording loaded</h3>
            <p>Upload a JSON file containing rrweb events to start replaying</p>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
